import tkinter as tkw=tk.Tk()w.title("gui programming seconday")w.geometry("300x300")var=tk.StringVar()#定义文本框e=tk.Entry(master=w,show="#")e.pack()#定义相关命令函数def insert_point():	t.insert("insert",e.get())def insert_end():	t.insert("end",e.get())#定义按钮b1=tk.Button(w,text="insert point",width=15,height=2,command=insert_point)b1.pack()b2=tk.Button(w,text="insert end",width=15,height=2,command=insert_end)b2.pack()#定义Textt=tk.Text(w,height=30,bg="steelblue")t.pack()w.mainloop()