#import modulesimport pandas as pdimport numpy as npfrom sklearn.model_selection import train_test_split#import metadatadata=pd.read_csv('Credit Card Marketing.csv')#delete duplicate rows by iddata1=data.drop_duplicates(['ID'])#whether oversamplingtarget_flag_freq=data1['target_flag'].value_counts()#positive:negative=1:1 -> 3:1#sampling by target_flaggrps=data1.groupby('target_flag')data1_N=grps.get_group('N')data1_Y=grps.get_group('Y')data1_N_s=data1_N.sample(frac=0.1,axis=0)data1_Y_s=data1_Y.sample(frac=0.3,axis=0)data1_s=pd.concat([data1_N_s,data1_Y_s])train,test=train_test_split(data1_s,test_size=0.3)#format target_flagtrain['target_flag']=train['target_flag'].apply(lambda x:1 if x=='N' else 0)test['target_flag']=test['target_flag'].apply(lambda x:1 if x=='N' else 0)